import Vue from 'vue'
import App from './App.vue'
Vue.config.productionTip = false;
import axios from 'axios';
Vue.config.errorHandler = function (err) {
  // Get the error details
  const errorMessage = err.message;
  const stackTrace = err.stack;
  console.log("error from vue event handler ===>", errorMessage, stackTrace);
  console.log(process.env)
  // Create the JIRA ticket
  const data = {
    fields: {
      project: {
        key: 'EE'
      },
      summary: errorMessage,
      description: stackTrace.toString(),
      issuetype: {
        name:"bug"
      }
    }
  };

  axios.post('https://arhammshahidd.atlassian.net/rest/api/2/issue/10011', data, {
    auth: {
      username: 'arhamq15@gmail.com',
      password: 'GqpKhaEKK4QX46M7QzEN14D6',
    },
    headers:{
      'X-Atlassian-Token':'no-check',
    }
  })
  .then(response => {
    console.log('JIRA ticket created successfully', response)
  })
  .catch(error => {
    console.error('Error creating JIRA ticket:', error)
  });
}
new Vue({
  render: h => h(App),
}).$mount('#app')
