import HelloWorld from './components/HelloWorld.vue';
import axios from 'axios';
import Vue from 'vue';
Vue.config.productionTip = false;
export default {
  name: 'App',
  components: {
    HelloWorld
  },
  methods: {
    testError() {
      console.log("Chap");
    }
  },
  mounted() {
    console.log("whatup");
    Vue.config.errorHandler = err => {
      // Get the error details
      const errorMessage = err.message;
      const stackTrace = err.stack;
      console.error("error from vue event handler ===>", errorMessage, stackTrace);
      // Create the JIRA ticket
      const data = {
        fields: {
          project: {
            key: process.env.JIRA_PROJECT_KEY
          },
          summary: errorMessage,
          description: stackTrace.toString(),
          issuetype: {
            name: 'bug'
          }
        }
      };
      axios.post(`https://demo.local.com/api/issues`, data).then(response => {
        console.log('JIRA ticket created successfully', response);
      }).catch(error => {
        console.error('Error creating JIRA ticket:', error);
      });
    };
  }
};
